import React, { useEffect, useState } from "react";


const AboutContext = React.createContext({
    name: "",
    setName: () => {}
});



const AboutContextProvider = ({value, children}) => {
    const [name, setName] = useState("");

    const setMyName = (newName) => {
        setName(newName);
        console.log("set my name: ", newName)
      };

    useEffect(() => {
        console.log("name updated")
    }, [name])
    return (
        <AboutContext.Provider value={{name, setName: setMyName}}>
            {children}
        </AboutContext.Provider>
    )
}
export { AboutContext, AboutContextProvider }
